.bg {
  width: 100%;
  left: 0;
  top: 0;
  height: 100%;
  background: #000;
}

#watch {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%) scale(0.28);
  border-radius: 50%;
  width: 450px;
  height: 450px;
  background: radial-gradient(grey, black 70%);
  color: #fff;
  font: bold 54px Arial, sans-serif;
}

.digit {
  position: absolute;
  width: 120%;
  height: 120%;
  margin: -10%;
  text-align: center;

  span {
    display: block;
  }
}

@for $i from 1 through 12 {
  .digit:nth-child(#{$i}) {
    transform: rotate(30deg * $i);

    span {
      transform: rotate(-30deg * $i);
    }
  }
}

#mickey,
#hours,
#minutes {
  width: 100%;
  height: 100%;
  position: absolute;
  background: no-repeat center center;
  background-size: contain;
}

#mickey {
  background-size: 70%;
  background-position: 45% 85%;
  z-index: 50;
}

#hours,
#minutes,
#minutes:after {
  position: absolute;
  width: 100%;
  height: 100%;
  background: no-repeat top center;
  background-size: 90px;
  background-position: center 5%;
  transition: transform 1s;
}

#hours {
  z-index: 10;
}

#minutes {
  z-index: 100;
}

#minutes:after {
  content: '';
  display: block;
  animation: seconds infinite 1s;
}

@keyframes seconds {
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(.95);
  }
  100% {
    transform: scale(1);
  }
}

